import statisticsfrom scipy.stats import skewimport matplotlib.pyplot as pltimport numpyf = open('var10.txt')lines_from_file = []for line in f.read().splitlines():    lines_from_file.append(float(line))count_of_lines = len(lines_from_file)def lineplot(x_data, y_data, x_label="", y_label="", title=""):    _, ax = plt.subplots()    ax.plot(x_data, y_data, lw=2, color='#000000', alpha=1)    ax.set_title(title)    ax.set_xlabel(x_label)    ax.set_ylabel(y_label)def boxplot(x_data, y_data, base_color="#000000", median_color="#297083", x_label="", y_label="", title=""):    _, ax = plt.subplots()    ax.boxplot(y_data               , patch_artist=True               , medianprops={'color': median_color}               , boxprops={'color': base_color, 'facecolor': base_color}               , whiskerprops={'color': base_color}               , capprops={'color': base_color})    ax.set_ylabel(y_label)    ax.set_xlabel(x_label)    ax.set_title(title)def histogram(data, n_bins, cumulative=False, x_label="", y_label="", title=""):    _, ax = plt.subplots()    ax.hist(data, bins=n_bins, cumulative=cumulative, color='#000000')    ax.set_ylabel(y_label)    ax.set_xlabel(x_label)    ax.set_title(title)lineplot([i for i in range(100)], lines_from_file[:100], '', 'Значения из файла')boxplot([i for i in range(count_of_lines)], lines_from_file)histogram(lines_from_file, 3)histogram(lines_from_file, 15)histogram(lines_from_file, 30)# plt.show()sum_of_lines = sum(lines_from_file)avg_score = sum_of_lines / count_of_linesprint('Среднее значеение: ', avg_score)def stdev(nums):    diffs = 0    for n in nums:        diffs += (n - avg_score) ** (2)    return (diffs / (len(nums) - 1)) ** (0.5)print('Стандартное отклонение: ', stdev(lines_from_file))print('Дисперсия: ', stdev(lines_from_file)**2)sorted_lines = sorted(lines_from_file)middle = (count_of_lines / 2) + 0.5print('Медиана: ',sorted_lines[int(middle)])numpy.linspace()